        this.index = 4;
        setTimeout(() => {
          this.imgListLeft = -500 * 4; // 图片宽度500px，共4张图片
          this.transitionStyle = "1s ease";
        }, 0);
      } else {
        this.imgListLeft = -500 * this.index; // 图片宽度500px
        this.transitionStyle = "1s ease";
      }
    },
    moveRight() {
      this.index++;
      this.imgListLeft = -500 * this.index; // 图片宽度500px
      this.transitionStyle = "1s ease";
      if (this.index === 4) {
        this.index = 0;
        setTimeout(() => {
          this.imgListLeft = 0;
          this.transitionStyle = "none";
        }, 500);
      }
    },
    moveTo(idx) {
      this.index = idx;
      this.imgListLeft = -500 * idx; // 图片宽度500px
      this.transitionStyle = "1s ease";
    },
    stopAutoplay() {
      clearInterval(this.autoplay);
    },
    startAutoplay() {
      clearInterval(this.autoplay);
      this.autoplay = setInterval(this.moveRight, 2000);
    }
  },
  mounted() {
    this.autoplay = setInterval(this.moveRight, 2000);
  }
};
